import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props){
  super(props);
  this.state={
    list:[],
    itemInput:'',
    descriptionInput:'',
    ownerInput:''
  }
}

  componentDidMount(){
    fetch('http://localhost:3000')
    .then(res=>res.json())
    .then((data)=>{
      this.setState({list:data}, ()=>{
        console.log(this.state.list)
      })
    })
  }

  clearForm =()=>{
    this.setState({
      itemInput:'',
      descriptionInput:'',
      ownerInput:''
    }, ()=>{console.log('clear')})
  }


  onInputChange=(e)=>{
    this.setState({[e.target.name]:e.target.value},()=>{
      console.log(this.state.itemInput)
    })
  }

  onSubmit=(e)=>{
    const {itemInput, descriptionInput, ownerInput} =this.state;
    fetch('http://localhost:3000/add',{
      method:'POST',
      body:JSON.stringify({
        item:itemInput,
        description:descriptionInput,
        owner:ownerInput
      }),
      headers:{'Content-Type':'application/json'}
    })
    this.clearForm();
  }

  render() {
    const {list} = this.state;
    const userList = list.map((item, i)=>{
      return <li key={i} style={{listStyle:'none'}}>{item.item}</li>
    });

    return (
      <div className="App">
      <h3>Item:</h3>
      <input 
      style={{background:'black', color:'white', fontSize:'2rem'}} 
      type='text' 
      id='textInput' 
      value={this.state.itemInput} 
      onChange={this.onInputChange} 
      name ='itemInput'
      />
      <h3>description:</h3>
      <input 
      style={{background:'black', color:'white', fontSize:'2rem'}} 
      type='text' 
      id='descriptionInput' 
      value={this.state.descriptionInput} 
      onChange={this.onInputChange} 
      name ='descriptionInput'
      />
      <h3>Assign To:</h3>
      <input 
      style={{background:'black', color:'white', fontSize:'2rem'}} 
      type='text' 
      id='ownerInput' 
      value={this.state.ownerInput} 
      onChange={this.onInputChange} 
      name ='ownerInput'
      />

      <button type='submit' onClick={this.onSubmit} style={{fontSize:'2rem', backgroundColor:'red'}}>Add</button>
       <ul>
       {this.state.list.map((item,i)=>{
        return <li key={i} style={{listStyle:'none'}}>{item.item}</li>
       })}
       </ul>
      </div>
    );
  }
}

export default App;
